/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {

    var bbb = api.state.baseM; //202406

    // 18개월 배열 생성
    var currentMonth = parseInt(bbb);
    var numMonths = 18;
    var yearmonths = [];

    for (var i = 0; i < numMonths; i++) {
        yearmonths.push(currentMonth.toString());
        currentMonth++;
        if (currentMonth % 100 > 12) {
            currentMonth += 88;
        }
    }

    console.log('yearmonths', yearmonths);

    var arr = [];

    var real = {
        "columnInfo": {
            "width": 100,
            "header": {
                "text": 'WBS'
            }
        },
        "layoutInfo": {
            "width": 100,
            "column": 'wbs'
        }
    };

    arr.push(real);



    for (let i = 0; i < yearmonths.length; i++) {
        const newObj = {
            "columnInfo": {
                "width": 100,
                "header": {
                    "text": yearmonths[i]
                }
            },
            "layoutInfo": {
                "width": 100,
                "column": `month${i+1}`
            }
        };

        arr.push(newObj);
    }

    var real2 = {
        "columnInfo": {
            "width": 100,
            "header": {
                "text": 'Beyond18'
            }
        },
        "layoutInfo": {
            "width": 100,
            "column": 'beyond18'
        }
    };

    arr.push(real2);

    var real3 = {
        "columnInfo": {
            "width": 100,
            "header": {
                "text": 'Forecast PTC'
            }
        },
        "layoutInfo": {
            "width": 100,
            "column": 'rpt_forecast_ptc'
        }
    };
    arr.push(real3);
    var real4 = {
        "columnInfo": {
            "width": 100,
            "header": {
                "text": 'Variance'
            }
        },
        "layoutInfo": {
            "width": 100,
            "column": 'variance'
        }
    };
    arr.push(real4);
    return arr;
}
