sp_widget.client_script:
api.controller=function($scope, $location) {
  /* widget controller */
  var c = this;

	c.data.showAddInfo = true;
	c.data.showRFQEntry = true;
	c.data.showQuotation = true;

	c.data.checkedSDG = false;
	c.data.checkedDXP = false;
	c.data.checkedAI = false;
	c.data.checkedDES = false;
	c.data.checkedSmart = false;

	$scope.displayScreen = function(idx) {
		if(idx == 1) {
			c.data.showAddInfo = !c.data.showAddInfo;
		} else if(idx == 2) {
			c.data.showRFQEntry = !c.data.showRFQEntry;
		} else if(idx == 3) {
			c.data.showQuotation = !c.data.showQuotation;
		}
	}

	$scope.btnClickSOSearch = function() {

		console.log("BTN : SO Search");
		c.server.update().then(function(response) {
			console.log('Server response:', response);
		});
	}

	$scope.btnClickSOInfo = function() {
		console.log("BTN : SO Info");
		c.server.update().then(function(response) {
