	$scope.gridDetailBtnClick = function(idx) {
		var v = {
			'sys_id' : '', 
			'number' : '', 
			'wbs_6' : '', 
			'ms_code' : '', 
			'description' : 'test description', 
			'quantity' :'', 
			'unit_price' :'', 
			'extended_price' : '', 
			'unit_cost' : '',
			'extended_cost' : '',
			'unit_mp' : '', 
			'mp_amount' : '', 
			'vendor' : '', 
			'oi_report_num' : '',
			'oi_report_sys_Id' :data.oiSysId
		}

		c.data.gridBtnType = '';
		if(idx == 1) {
			c.data.gridBtnType = 'add';
			dpDetail.addRow(v);
		} else if(idx == 2) {
			c.data.gridBtnType = 'insert';
			dpDetail.insertRow(0, v);
		} else if(idx == 3) {
			c.data.gridBtnType = 'delete';
			//gvDetail.deleteSelection(true);
			var curr = gvDetail.getCurrent();
  		dpDetail.removeRow(curr.dataRow);
		} else if(idx == 4) {
			c.data.gridBtnType = 'save';
		}



		console.log(dpDetail.getRowCount());


		c.server.update().then(function(response) {
			console.log('Server response:', response);
		});
	}

	/* 최초 초기화 */
	c.server.update().then(function(response) {
		console.log('Init screen rsponse:', response);

		c.data.initScreen = false;
		c.data.initOiReport = false;
		c.data.initRfqEntry = false;
		c.data.initQuotation = false;

		c.data.showBtnItemDetail = true;
		c.data.showBtnCancel = true;
		c.data.showBtnSave = true;

		if(Number(c.data.oiStateCode) == "1") {
			console.log("STATE:" + c.data.oiState);
			c.data.showBtnConfirmReq = false;
			c.data.showBtnApprovalReq = false;
		} else {
			console.log("STATE: NONE");
			c.data.showBtnConfirmReq = true;
			c.data.showBtnApprovalReq = true;
		}

		var loopCnt = 0;
		var timerid = setInterval(function() {
			loopCnt = loopCnt + 1;
			if(loopCnt > 30) {
				clearTimeout(timerid);
				alert('Can not load REALGRID');
			}

			if (checkRealGrid()) {
				clearTimeout(timerid);
				drawRealGrid();
			} 
		}, 100);

		//$scope.serviceCall = testService.showResult();
		//console.log("$scope.serviceCall: "+$scope.serviceCall);
	});	
};

