	$scope.clickProjectType = function(item) {
		if(item.type == 1) {
			c.data.prjType1Nm = item.name;
			c.data.prjType1Desc = item.desc;
			c.data.prjType1Code = item.code;
			c.data.prjType1SysId = item.sysid;
		} else if(item.type == 2) {
			c.data.prjType2Nm = item.name;
			c.data.prjType2Desc = item.desc;
			c.data.prjType2Code = item.code;
			c.data.prjType2SysId = item.sysid;
		} else if(item.type == 3) {
			c.data.prjType3Nm = item.name;
			c.data.prjType3Desc = item.desc;
			c.data.prjType3Code = item.code;
			c.data.prjType3SysId = item.sysid;
		}

		c.data.modalProjectType = false;
		c.data.modalTypePrjType1 = false;
		c.data.modalTypePrjType2 = false;
		c.data.modalTypePrjType3 = false;

		modalProjectType.style.display = 'none';
	}

