	c.getUrlParameter = function(param) {
		return $location.search()[param];
	};

	// url 에서 sys_id 가져온다. 
	var urlSysId = c.getUrlParameter('sys_id');
	console.log('Value of yourParameterName:', urlSysId);
	c.data.urlSysId = urlSysId;

	var data = {
		message: "Hello from Sender Widget",
		timestamp: new Date(),
		table: "x_yergb_bpm_oi_report",
		//table: "x_yergb_bpm_quotation",
		sys_id: urlSysId
	};

	// 이벤트를 발생시켜 데이터를 전송
	$rootScope.$broadcast('dataFromSenderWidget', data);

	function resizeIframe() {
		var iframe = document.getElementById('myIframe');
		if (iframe) {
			var windowHeight = window.innerHeight;
			iframe.style.height = Math.max(windowHeight * 0.45, 200) + 'px';
		}
	}

	// Add event listener to resize iframe when content loads
	$window.onload = function() {
		resizeIframe();
	};

	$window.addEventListener('message', function(event) {
		if (event.data.type === 'updateIframeSrc') {
      location.reload();
    }
  });

	// Add event listener to window resize event
	$window.addEventListener('resize', function() {
		resizeIframe();
	});

	// Call resizeIframe on initial load to set the initial height
	resizeIframe();





	function checkRealGrid() {
		if(RealGrid) {
			return true;
		} 

		return false;
	}

	var dpDetail;
	var gvDetail;
	var containerDetail;
	function drawRealGrid() {
		containerDetail = document.getElementById('gridContainer');
		dpDetail = new RealGrid.LocalDataProvider(false);
		gvDetail = new RealGrid.GridView(containerDetail);
		gvDetail.setDataSource(dpDetail);

		dpDetail.setFields([
			/*{
				fieldName: 'KorName',
				dataType: 'text',
			},
			{
				fieldName: 'Age',
				dataType: 'number',
			},*/

			'rowStat',
			'sys_id',
			'number',
			'wbs_6',
			'ms_code',
			'description',
			'quantity',
			'unit_price',
			'extended_price',
			'unit_cost',
			'extended_cost',
			'unit_mp',
			'mp_amount',
			'vendor',
			'oi_report_num',
			'oi_report_sys_Id'
		]);

		gvDetail.setColumns([
			/*{
				"name": "sys_id",
				"fieldName": "sys_id",
				"type": "data",
				"width": 0,
				"header": {
					"text": "sysid"
				},
			},*/
			{
				"name": "number",
				"fieldName": "number",
				"type": "data",
				"width": 100,
				"header": {
					"text": "Number"
				},
				"editable": false,
			},
			{
				"name": "wbs_6",
				"fieldName": "wbs_6",
				"type": "data",
				"width": 100,
				"header": {
					"text": "WBS 6"
				},
			},
			{
				"name": "ms_code",
				"fieldName": "ms_code",
				"type": "data",
