  function initGrid() {
		console.log(RealGrid);
    gridView = new RealGrid.GridView('gridContainer');
    dp = new RealGrid.LocalDataProvider();

    gridView.setDataSource(dp);


    // 데이터 설정
    dp.setFields(grid_fields);
    gridView.setColumns(grid_columns);
    gridView.footers.visible = true;
    gridView.commitEditor();
    gridView.undoable = true;

    gridView.columnByName("sys_id").visible =  false;

    gridView.editOptions.commitWhenLeave = true;
    gridView.displayOptions.fitStyle = 'evenFill'

    gridView.setColumnLayout(grid_layouts);
    dp.softDeleting = false;
	}
















    c.getUrlParameter = function(param) {
        return $location.search()[param];
    };

    var paramValue = c.getUrlParameter('sys_id');
    c.data.paramValue = paramValue;

    $scope.btnClickComplete = function() {
        c.data.operation = 'updateState';
        c.server.update().then(function(response) {
            console.log('Server response:', response);
            location.reload();
        }).catch(function(error) {
            console.error('Error updating state:', error);
            alert('Error updating state');
        });
    };

    // Function to save a single field
    $scope.saveField = function(fieldName, fieldValue) {
        if (!fieldValue) {
            alert('Field value cannot be empty');
            return;
        }
        c.data.operation = 'saveField';

        c.server.update().then(function(response) {
            location.reload();
        }).catch(function(error) {
            console.error('Error saving field:', error);
            alert('Error saving field');
        });
    };

    $window.addEventListener('message', function(event) {
        if (event.data.type === 'updateIframeSrc') {
            location.reload();
            //var iframe = document.getElementById('myIframe');
            // iframe.src = event.data.newSrc;
            // console.log('Iframe src updated to:', event.data.newSrc);
        }
    });

    // Function to handle click event
    $scope.handleClick = function() {	
        if (c.data.rfq_entry_id) {
            window.location.href = "/sp?id=rfqentry&table=x_yergb_bpm_rfq_entry&sys_id=" + c.data.rfq_entry_id; // Redirect to the URL
        } else {
            alert('no rfq record')
        }
    };

    // Function to dynamically adjust iframe height
    function resizeIframe() {
        var iframe = document.getElementById('myIframe');
        if (iframe) {
            var windowHeight = window.innerHeight;
            iframe.style.height = Math.max(windowHeight * 0.45, 200) + 'px';
        }
    }

    // Add event listener to resize iframe when content loads
    $window.onload = function() {
      resizeIframe();
			//initGrid();
    };

    // Add event listener to window resize event
    $window.addEventListener('resize', function() {
        resizeIframe();
    });

    // Call resizeIframe on initial load to set the initial height
    resizeIframe();


		setTimeout(function() {
