(function() {

	data.basemonth = $sp.getParameter("basemonth");
	if(!data.basemonth){
		data.basemonth = '202406';
	}
	function getGridData(item_sysid, basemonth) {
        var gritem = new GlideRecord("x_yergb_bpm_cost");
        gritem.orderBy("number");
        gritem.query();
        var tableinfo = [];
        while (gritem.next()) {
			tableinfo.push({
				'sys_id': gritem.sys_id.getDisplayValue(),
				'wbs': gritem.wbs.getDisplayValue(),
				'__type': '참고',
				'in_charge': gritem.in_charge.getDisplayValue(),
				'activity_type': gritem.activity_type.getDisplayValue(),
				'activity_rate': parseInt(gritem.activity_rate.getDisplayValue().replace(/,/g, ''), 10) || 0,
				'total_amount': parseInt(gritem.total_amount.getDisplayValue(), 10) || 0,
				'__delete' : 'Del',
				'__insert' : 'Ins',
			});
        }
        return tableinfo;
	}

	function getData(basemonth) {
		var gritem = new GlideRecord("x_yergb_bpm_budget_plan");
		gritem.addEncodedQuery('base_month='+basemonth+'^plan_type=prf');
		gritem.orderBy("project");
		gritem.query();

		var tableinfo = [];
		while (gritem.next()) {
			tableinfo.push({
				'sys_id' : gritem.sys_id.getDisplayValue(),
				'number' : gritem.number.getDisplayValue(),
				'project_no' : gritem.project.getDisplayValue(),
			  'contractor' : gritem.customername.getDisplayValue(),
				'project_name' : gritem.project_name.getDisplayValue(), 
				'pm' : gritem.pm_name.getDisplayValue(), 
				'contract_amt' : gritem.contract.getDisplayValue(), 
				'ptc_last_month' : gritem.intake_last_month.getDisplayValue(), 
				'forecast_ptc' : isNaN(gritem.forecast_ptc) ? 0 : gritem.forecast_ptc.getDisplayValue(), 
				'state': gritem.state.getDisplayValue(),
				'variance' : 0, 
				'poc_percentage' : 'N/A',
			})
		}
		return tableinfo;
    }

	data.gridData = getData(data.basemonth);

		/* Project type Modal */
		if(input && input.modalProject) {
			data.itemsProject = [];

			var grPjt = new GlideRecord('x_yergb_bpm_project');
			grPjt.query();
			while(grPjt.next()) {
				data.itemsProject.push({
					name : grPjt.name.toString(),
					code : grPjt.number.toString(), 
					sys_id : grPjt.sys_id.toString()
				});
			}
		}

		/* Project type Modal */
		if(input && input.modalPM) {
			data.itemsPMNames = [];
			var grPm = new GlideRecord('sys_user');
			grPm.query();
			while(grPm.next()) {
				data.itemsPMNames.push({
					name : grPm.user_name.toString(),
					department : grPm.department.toString(), 
					sys_id : grPm.sys_id.toString()
				});
			}
		}
			/* Project type Modal */
		if(input && input.modalState) {
			data.itemState = [];
			var grPm = new GlideRecord('sys_choice');
			grPm.addEncodedQuery('name=x_yergb_bpm_budget_plan^element=state');
			grPm.query();
			while(grPm.next()) {
				data.itemState.push({
					label : grPm.label.toString(),
					value : grPm.value.toString()
				});
			}
		}

	/* Search*/
		if(input && input.SearchState) {
			getSearchData(data.basemonth);
			function getSearchData(basemonth) {
				var gritem = new GlideRecord("x_yergb_bpm_budget_plan");
				gritem.addEncodedQuery(input.myQuery);
				gritem.orderBy("project");
				gritem.query();

				var searchinfo = [];
				while (gritem.next()) {
					searchinfo.push({
					'sys_id' : gritem.sys_id.getDisplayValue(),
					'number' : gritem.number.getDisplayValue(),
					'project_no' : gritem.project.getDisplayValue(),
				  'contractor' : gritem.customername.getDisplayValue(),
					'project_name' : gritem.project_name.getDisplayValue(), 
					'pm' : gritem.pm_name.getDisplayValue(), 
					'contract_amt' : gritem.contract.getDisplayValue(), 
					'ptc_last_month' : gritem.intake_last_month.getDisplayValue(), 
					'forecast_ptc' : isNaN(gritem.forecast_ptc) ? 0 : gritem.forecast_ptc.getDisplayValue(), 
					'state': gritem.state.getDisplayValue(),
					'variance' : 0, 
