api.controller = function($scope, $location, $window) {

    /* widget controller */
    var c = this;
    c.data.readStatus = true;
    // Function to get URL parameters
    c.getUrlParameter = function(param) {
        return $location.search()[param];
    };
    // Function to handle click event
    $scope.handleClick = function() {
        if (c.data.budget_key) {
            window.location.href = "/ybpm?id=epic_09_ui_prj&number=" + c.data.number + "&table=x_yergb_bpm_budget_plan&sys_id=" + c.data.budget_key; // Redirect to the URL
        }
    };
    // Example: Retrieve a specific parameter
    var paramValue = c.getUrlParameter('number');
    c.data.paramValue = paramValue;

    $scope.clickYProd = function() {
        window.location.href = "/ybpm?id=epic_09_ui_budget_y_product&number=" + c.data.paramValue;
    };
    $scope.clickBOP = function() {
        window.location.href = "/ybpm?id=epic_09_ui_budget_bop&number=" + c.data.paramValue;
    };
    $scope.clickManHour = function() {
        window.location.href = "/ybpm?id=epic_09_ui_budget_man_hour&number=" + c.data.paramValue;
    };
    $scope.clickOther = function() {
        window.location.href = "/ybpm?id=epic_09_ui_budget_other&number=" + c.data.paramValue;
    };
    $scope.clickSave = function() {

    };

    $scope.clickLoadData = function() {
        // Call the server script
        c.server.update($scope).then(function(response) {
            if (!c.data.budget_key) {
                location.reload();
            } else {
                window.location.href = "/ybpm?id=epic_03_ui_init&number=" + c.data.number + "&table=x_yergb_bpm_budget_plan&sys_id=" + c.data.budget_key;
            }
        });
    };
    $scope.clickApproval = function() {
        c.data.approve = 'Requested';
        // Call the server script
        c.server.update($scope).then(function(response) {
            alert("승인요청을 보냈습니다.");
        });
        window.location.href = "/ybpm?id=epic_03_ui_init&number=" + c.data.number + "&table=x_yergb_bpm_budget_plan&sys_id=" + c.data.budget_key;
    };
    $scope.clickMSF = function() {
        alert("Working..... In progress develop..MSF");
    };
    //grid save 할 때 전체페이지 reload
    $window.addEventListener('message', function(event) {
        if (event.data.type === 'updateIframeSrc') {
            location.reload();
        }
    });

    // Function to dynamically adjust iframe height
    function resizeIframe() {
        var iframe = document.getElementById('myIframe');
        if (iframe) {
            var windowHeight = window.innerHeight;
            iframe.style.height = Math.max(windowHeight * 0.5, 200) + 'px';
        }
    }

    // Add event listener to resize iframe when content loads
    $window.onload = function() {
        resizeIframe();
    };

    // Add event listener to window resize event
    $window.addEventListener('resize', function() {
        resizeIframe();
    });

    // Call resizeIframe on initial load to set the initial height
    resizeIframe();


