sp_widget.client_script:
api.controller=function($scope, $location) {
  /* widget controller */
  var c = this;

	c.getUrlParameter = function(param) {
		return $location.search()[param];
	};

	var paramValue = c.getUrlParameter('sys_id');
  console.log('Value of yourParameterName:', paramValue);
  c.data.paramValue = paramValue;


	debugger

	$scope.clickComplete = function() {

    c.data.newState = 9;
    c.server.update().then(function(response) {
    	console.log('Server response:', response);
    	location.reload();
		});
	};


};

