sp_widget.client_script:
api.controller=function($scope, $rootScope, $window, $location) {
  /* widget controller */
  var c = this;

	/* Server master values */
	c.data.approvalId = '';
	c.data.oiSysId = '';
	c.data.rfqSysId = '';
	c.data.quotSysId = '';


	/* Header button variable */
	c.data.showBtnReject = true;
	c.data.showBtnApprove = true;

	c.data.btnHeaderType = '';
	c.data.initScreen = true;
	c.data.initOiReport = true;
	c.data.initRfqEntry = true;
	c.data.initQuotation = true;


	/* Heaer button events */
	$scope.btnHeaderClick = function(type) {
		console.log('CLIENT HEADER BTN : ' + type);
		c.data.btnHeaderType = type;
		var up = true;
		if(type == 'Reject') {
			console.log('Comment :' + c.data.approveComments + ':');
			if( (c.data.approveComments == '') || (c.data.approveComments == undefined) ) {
				alert('Please check comments for reject!');
				up = false;
			}
		} /*else if(type == 'Approve') {

		}*/
		if(up) {
			c.server.update().then(function(response) {
				console.log('Header button click rsponse:', response);
				if(c.data.btnHeaderType=='Reject') {
					alert('Reject process done.');
				} else if(c.data.btnHeaderType=='approved') {
					alert('Approve process done.');
				}

				c.data.showBtnReject = false;
				c.data.showBtnApprove = false;
				c.data.btnHeaderType = '';
			});
		}
	}

	/* common button */
	$scope.btnPostClick = function() {
		console.log('POST button Clicked');

		/*
		c.server.update().then(function(response) {
			console.log('Post button click rsponse:', response);
		});
		*/
	}

	$scope.btnRfqNumSearch = function() {
		console.log('BTN : RFQ Search');

		/*
		c.server.update().then(function(response) {
			console.log('RFQ Search button click rsponse:', response);
		});
		*/
	}

	$scope.btnRfqNumInfo = function() {
		console.log('BTN : RFQ Info');

		/*
		c.server.update().then(function(response) {
			console.log('RFQ Info button click rsponse:', response);
		});
		*/
	}


	c.getUrlParameter = function(param) {
		return $location.search()[param];
	};

	// url 에서 sys_id 가져온다. 
	var urlSysId = c.getUrlParameter('sys_id');
	console.log('Value of yourParameterName:', urlSysId);
	c.data.urlSysId = urlSysId;

/*	var data = {
		message: "OI report approval screen stepper",
		timestamp: new Date(),
		table: "x_yergb_bpm_oi_report",
		//table: "x_yergb_bpm_quotation",
		sys_id: urlSysId
	};

	// 이벤트를 발생시켜 데이터를 전송
	$rootScope.$broadcast('dataFromSenderWidget', data);
	*/

	function resizeIframe() {
		var iframe = document.getElementById('myIframe');
		if (iframe) {
			var windowHeight = window.innerHeight;
			iframe.style.height = Math.max(windowHeight * 0.45, 200) + 'px';
		}
	}

	// Add event listener to resize iframe when content loads
	$window.onload = function() {
		resizeIframe();
	};

	$window.addEventListener('message', function(event) {
		if (event.data.type === 'updateIframeSrc') {
      location.reload();
    }
  });

	// Add event listener to window resize event
	$window.addEventListener('resize', function() {
		resizeIframe();
	});

	// Call resizeIframe on initial load to set the initial height
	resizeIframe();

	/*function formatNumber(str) {
    var t = str.replace(/,/g, '').replace(/\D/g, '');
    return t.replace(/\B(?=(\d{3})+(?!\d))/g, ',');
	}*/

	/* 최초 초기화 */
	c.server.update().then(function(response) {
		console.log('Init screen rsponse:', response);

		c.data.initOiReport = false;
		c.data.initRfqEntry = false;
		c.data.initQuotation = false;

		c.data.initScreen = false;
		c.data.initOiReport = false;
		c.data.initRfqEntry = false;
		c.data.initQuotation = false;

		/*c.data.mpAmount = formatNumber(c.data.mpAmount);
		c.data.mpGap = formatNumber(c.data.mpGap);
		c.data.oiAmtTC = formatNumber(c.data.oiAmtTC);
		c.data.mpGapPerc = formatNumber(c.data.mpGapPerc);
		c.data.oiAmtLC = formatNumber(c.data.oiAmtLC);
		c.data.gpAmt = formatNumber(c.data.gpAmt);
		c.data.totEstimCost = formatNumber(c.data.totEstimCost);
		c.data.gpPer = formatNumber(c.data.gpPer);*/



		/*var xx = 'APPID : ' + c.data.approvalId + '\nOIID : ' + c.data.oiSysId;
		alert(xx);*/
	});
};

