sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	console.log("USER NAME : " + gs.getUser().getDisplayName());
	console.log("SYS ID : " + gs.getUser().getID());

	/*
	x_yergb_bpm: d2cdb552db252200a6a2b31be0b8f5ee
	x_yergb_bpm: 007a44badba52200a6a2b31be0b8f525
	*/
	if(input && (input.initScreen==true)) {
		data.approvalId = $sp.getParameter("sys_id");
	}

	console.log('APPR SYS ID : ' + data.approvalId);

	// TEST
	//data.approvalId = '007a44badba52200a6a2b31be0b8f525';


	/* Get OI report information */
	if(input && (input.initOiReport==true) && !isEmpty(data.approvalId)) {
		var apprData = new GlideRecord('sysapproval_approver');
		apprData.initialize();
		apprData.addQuery('sys_id', data.approvalId);
		apprData.query();
		while(apprData.next()) {
			data.approverNm = apprData.approver.name.toString();
			data.approveComments = apprData.comments.toString();
			data.oiSysId = apprData.document_id.toString();
		}
		//console.log('OI SYS ID : ' + data.oiSysId);

		// TEST 
		//data.oiSysId = '049440b63b3e4210ec3cbf2a85e45afd0';
		//console.log('OI SYS ID : ' + data.oiSysId);

		var oiReportData = new GlideRecord('x_yergb_bpm_oi_report');
		oiReportData.addQuery('sys_id', data.oiSysId);
		oiReportData.query();
		while(oiReportData.next()) {
			data.version = oiReportData.version.toString();
			data.oiNum = oiReportData.number.toString();
			data.approveFor = oiReportData.number.toString();

			data.customerPONo = oiReportData.customer_po_no.toString();
			data.exRate = oiReportData.exchange_rate.getDisplayValue().toString();
			data.mpAmount = oiReportData.mp_amount_lc.getDisplayValue().toString();
			data.mpGap = oiReportData.mp_gap_lc.getDisplayValue().toString();
			data.oiAmtTC = oiReportData.oi_amount_tc.getDisplayValue().toString();
			data.mpGapPerc = oiReportData.mp_gap_percent.getDisplayValue().toString();
			data.oiAmtLC = oiReportData.oi_amount_lc.getDisplayValue().toString();
			data.gpAmt = oiReportData.gp_amount_lc.getDisplayValue().toString();
			data.totEstimCost = oiReportData.total_extended_cost_tc.getDisplayValue().toString();
			data.gpPer = oiReportData.gp_percent.getDisplayValue().toString();

			data.contractorNm = oiReportData.contractor_name.toString();
			data.contractorCode = oiReportData.contractor.getDisplayValue().toString();
			data.endUserNm = oiReportData.end_user_name.toStrring();
			data.endUserCode = oiReportData.end_user.getDisplayValue().toString();

			data.rfqSysId = oiReportData.rfq_entry.toString();
		}
	}

	if(input && (input.initOiReport==true) && !isEmpty(data.rfqSysId)) {
		var rfqEntryData = new GlideRecord('x_yergb_bpm_rfq_entry');
		rfqEntryData.addQuery('sys_id', data.rfqSysId);
		rfqEntryData.query();
		while(rfqEntryData.next()) {
			data.rfqNum = rfqEntryData.number.toString();
			data.salesMngNm = rfqEntryData.sales_manager.name.toString();
			data.salesMngCode = rfqEntryData.sales_manager.country.toString();
			data.endUserNm = rfqEntryData.end_user.name.toString();
		  //data.endUserCode = rfqEntryData.end_user.contract.toString();
			data.quotSysId = rfqEntryData.quotation.toString();
			data.yodaOpportunity = rfqEntryData.yoda_opportunity_no.toString();		
			data.contractorNm = rfqEntryData.contract.name.toString();
			data.contractorCode = rfqEntryData.contract_name.toString();
			data.projectNm = rfqEntryData.project_title.toString();
			data.currency = rfqEntryData.currency.code.toString();
		}
	}

	if(input && (input.initOiReport==true) && !isEmpty(data.quotSysId)) {
		var quotData = new GlideRecord('x_yergb_bpm_quotation');
		quotData.addQuery('sys_id', data.quotSysId);
		quotData.query();
		while(quotData.next()) {
			data.quotNum = quotData.number.toString();
			data.essSqmsNum = quotData.ess_sqms_no.toString();
		}
	}

	if(input) {
		var apData = new GlideRecord('sysapproval_approver');
		apData.initialize();
		apData.get(input.approvalId);		
		if(input.btnHeaderType=='Reject') {
			//console.log('SERVER BTN Reject');
			apData.setValue('state', 'rejected');
			apData.setValue('comments', data.approveComments);
		} else if(input.btnHeaderType=='Approve') {
			//console.log('SERVER BTN Approve');
			apData.setValue('state', 'approved');
		}

		apData.update();
	}



	/* Common functions */
	function isEmpty(str){
		if(typeof str == "undefined" || str == null || str == "")
			return true;
		else
			return false ;
	}


	function getValueOrEmptyString(value) {
		return typeof value === 'undefined' ? '' : value;
	}
})();
