sys_script.script:
(function executeRule(current, previous /*null when async*/) {


	// Setup Currency
	var exchange_rate = 1;
	if(current.currency != current.sales_manager.company.u_currency) {
		var grrate = new GlideRecord('x_yergb_bpm_bpm_exchange_rate');
		grrate.addQuery('source_currency',current.currency);
		grrate.addQuery('target_currency',current.sales_manager.company.u_currency);
		grrate.query();
		if(grrate.next()) {
			exchange_rate = grrate.exchange_rate;
			gs.info("Exchange_rate is not 1 : "+ exchange_rate + ", source:target = " + current.currency + " : " +  current.sales_manager.company.u_currency);
		}
	}

	current.currency_lc = current.sales_manager.company.u_currency;
	current.exchange_rate = exchange_rate;

	if(current.estimated_oi_amount != null) current.estimated_oi_amount_lc = current.estimated_oi_amount * exchange_rate;

	// Setup Profit Company
	if(current.currency != 'CNY' && current.sales_manager.company.u_currency == 'CNY') {
		current.profit_company = '350bb7023b83ca10ec3cbf2a85e45a71'; // YCN-SP
	}
	else 
		current.profit_company = current.sales_manager.company;

})(current, previous);
