                'planned_procurement': gritem.planned_procurement.getDisplayValue(),
                'plan_gr_date': gritem.plan_gr_date.getDisplayValue(),
                'project': project_sysid,
				'budget_type':'prod',
				'data_type':'item'
            });
        }
